import java.util.Scanner;
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		Scanner scan = new Scanner(System.in) ;
		int A = scan.nextInt() ;
		int B = scan.nextInt() ;
		
		System.out.println(A+B) ;
		System.out.println(A-B) ;
		System.out.println(A*B) ;
		System.out.println(A/B) ;
		System.out.println(A%B) ;
		
		

	}

}